import React from 'react';

const Card = ({ suit, rank }) => {
  // Назви файлів у твоїй папці формату c2.png, d2.png і т.д.
  const suitInitial = suit.charAt(0); // Перший символ масті
  const cardName = `${suitInitial}${rank}.png`;
  const cardPath = `/cards/${cardName}`;

  return (
    <div className="card">
      <img src={cardPath} alt={`${rank} of ${suit}`} />
    </div>
  );
};

export default Card;
